(()=>{const e=document.querySelector("#suar_container");!async function(){const t=await fetch("http://api.alquran.cloud/v1//surah"),n=await t.json();for(const t of n.data){const n="Medinan"==t.revelationType?"مَدَنِيَّة":"مَكِّيَّة";e.innerHTML+=`\n        <div id='surahItem' data-value=${t.number} class="item flex gap-2 p-2 w-1/4 border border-[#343a40] rounded cursor-pointer hover:bg-[#343a40]">\n            <div class="item_number flex items-center justify-center text-lg font-bold p-4 bg-gray border-transparent rounded shadow">\n                ${t.number}\n            </div>\n            <div class="item_details flex flex-col items-start justify-start px-2 w-full">\n                <div class="item_details_name flex items-center justify-between w-full">\n                    <div class="item_details_name_en text-lg font-semibold">${t.englishName}</div>\n                    <div class="item_details_name_ar text-lg font-light font-reemKufi">${t.name}</div>\n                </div>\n                <div class="item_details_tanzil text-xs font-light text-slate-200 flex items-center justify-between w-full">\n                    <div class="flex flex-col gap-2 items-start justify-start">\n                        <p>${t.englishNameTranslation}</p>\n                        <p>${t.revelationType}</p>\n                    </div>\n                    <div class="flex flex-col gap-2 items-start justify-start" dir='rtl'>\n                        <p dir='rtl'>${t.numberOfAyahs} آية</p>\n                        <p >${n}</p>\n                    </div>\n                </div>\n            </div>\n        </div>\n        `}}(),e.addEventListener("click",(e=>{const t=e.target.closest("#surahItem");if(t){const e=t.getAttribute("data-value");localStorage.setItem("surahNumber",e),window.location.href="../read"}}));const t=document.getElementById("header");window.addEventListener("scroll",(()=>{window.scrollY>0?t.classList.add("bg-[#1f2125]"):t.classList.remove("bg-[#1f2125]")}))})();